    //                 >
    //                 {label}
    //             </li>
    //         )
    //     })
    // }

    renderItems = (arr) => {
        return arr.map((item, index) => {

            const {title} = item.type;
            return (
                <li
                    key={index}>
                        {title}
                </li>
            )
        })
    }

    render () {
        const {types} = this.props;

        const listItems = this.renderItems(types);

        return (
            <TypeList>
                {listItems}
                {/* {
                    types.map(item => {
                        // console.log(item.type.name);
                        <CharTypesItem
                            name={item.type.name}
                        />
                    })
                } */}
            </TypeList>
        )
    }
}

export default CharTypes;
