import React, { Component } from 'react';
import styled from 'styled-components';

import ServicePokedex from '../../services';

import spinner from '../spinner/Spinner-5.gif';

const CharDescriptionWrapper = styled.div`
    display: flex;
    width: 100%;
    justify-content: center;
    align-items: center;
`;

const CharDescription = styled.div`
    position: sticky;
    top: 50%;
    transform: translate(25%, -50%);
    height: 60vh;
    display: flex;
    flex-direction: column;
    width: 60%;
    justify-content: center;
    align-items: center;
`;

export default class CharCardDescription extends Component {

    ServicePokedex = new ServicePokedex();

    constructor (props) {
        super(props);

        this.state = {
            char: [],
            error: false,
            imgURL: '',
            // nameNew: ''
        }
    }

    componentDidMount () {
        this.updateItem();

        const {charId} = this.props;
        const imgURL = `https://pokeres.bastionbot.org/images/pokemon/${charId}.png`;

        this.setState({
            imgURL
            // nameNew,
        });

    }

    componentDidUpdate (prevProps) {
        if (this.props.charId !== prevProps.charId) {
            this.updateItem();
        }
    }

    onCharDetailsLoaded = (char) => {
        this.setState({
            char
            // nameNew,
        });
    }

    updateItem () {
        const {charId, getData} = this.props;

        if (!charId) return;

        console.log(charId);
        // const nameNew = name[0].toUpperCase() + name.slice(1);

        getData(charId)
            .then(this.onCharDetailsLoaded(imgURL))
            .catch(() => this.onError());
    }

    onError () {
        this.setState({
            char: [],
            imgURL: '',
            error: true
        });
    }

    render () {
        const {char, imgURL} = this.state;
        const {name} = char;

        // attack, defence, hp, sp attack, sp defence, speed, weight, total moves 

        return (
            <CharDescriptionWrapper>
                <CharDescription>
                    <img src={imgURL} alt=""/>
                    <h3>{name}</h3>
                    <table>

                    </table>
                </CharDescription>   
            </CharDescriptionWrapper>
        )
    }
}
