import React, { Component } from 'react';
// import CharListItem from '../char-list-item';
import PropTypes from 'prop-types';

import ErrorMessage from '../error-message';

import ServicePokedex from '../../services';

import './char-list.css';

class CharList extends Component {

    ServicePokedex = new ServicePokedex();

    state = {
        data: null
    }

    componentDidMount () {
        const {getData} = this.props;

                 getData()
                .then((data) => {
                    this.setState({
                        data,
                        error: false
                    })
                })
                .catch()
        }

        componentDidCatch () {
            this.setState({
                data: null,
                error: true
            })
        }


    renderItems (arr) {
        return arr.map(item => {
            const {id} = item;
            const label = this.props.renderItem(item);
            // console.log(label);
            return (
                <li
                    key={id}
                    className='char-list__item'
                    // onClick={() => this.props.onItemSelected(id)}
                    label={label}></li>
            )
        });
    }

    render () {

        const {data: {results}} = this.state;
        console.log('some: ', results);
        const items = this.renderItems(results);
        // console.log(items);

        return (
            <div className="char-list">
                {items}
            </div>
        )
    }
}

