import React, { Component } from 'react';
import CharTypes from '../char-types';
// import ServicePokedex from '../../services';

import styled from 'styled-components';

import spinner from '../spinner/Spinner-5.gif';

import './char-list-item.css';

const Card = styled.div`
    width: 100%;
    display: flex;
    flex-direction: column;
    justify-content: center;
    align-items: center;
    background-color: black;

    border: 1px solid red;
`;

const ImgWrapper = styled.div`
    display: flex;
    width: 100%;
    justify-content: center;
    align-items: center;
    overflow: hidden;
`;

const Sprite = styled.img`
    width: 60%;
    object-fit: cover;
`;

const CardTitle = styled.h3`
    display: flex;
    width: 100%;
    justify-content: center;
    align-items: center;
    margin-top: 1rem;

    text-align: center;
    font-size: 1.25rem;
    font-weight: 400;
`;

export default class CharListItem extends Component{
    // ServicePokedex = new ServicePokedex();
    state = {
        nameNew: '',
        imgURL: '',
        charID: '',
        imgLoading: true,
        manyRequests: false,

        types: {}
    }

    componentDidMount () {
        const {name, url} = this.props;

        const charID = url.split('/')[url.split('/').length - 2];
        const imgURL = `https://pokeres.bastionbot.org/images/pokemon/${charID}.png`;
        const nameNew = name[0].toUpperCase() + name.slice(1)
        // const types = {}

        this.setState({
            nameNew,
            charID,
            imgURL
        })

    }

    render () {
        // const charTypes = (
        //     <CharTypes
        //         getData={this.SerrvicePokedex.getResources}/>
        // )

        return (
            <>
            <Card className="char-list__item">
                <ImgWrapper>
                    {
                        this.state.imgLoading ? (
                            <img
                                src={spinner}
                                style={{width: '5rem', height: '5rem'}}
                                className='spinner'/>
                        ) : null
                    }

                    <Sprite
                        className='char-img'
                        src={this.state.imgURL}
                        onLoad={() => this.setState({imgLoading: false})}
                        onError={() => this.setState({manyRequests: true})}
                        style={
                            this.state.manyRequests
                                ? {display: 'none'}
                                : this.state.imgLoading
                                ? null
                                : {display: 'block'}
                        }
                        />
                    {this.state.manyRequests ? (
                        <h4>To many requests</h4>
                    ) : null}
                </ImgWrapper>
                <CardTitle className='char__title'>{this.state.nameNew}</CardTitle>
                <CharTypes/>

            </Card>            
        </>
        )

    }
}
