import React, { Component } from 'react';
// import CharListItem from '../char-list-item';
// import PropTypes from 'prop-types';

import ErrorMessage from '../error-message';

// import ServicePokedex from '../../services';

import './char-list.css';

class CharList extends Component {

    // renderItems (arr) {
    //     return arr.map(item => {
    //         const {id} = item;
    //         const label = this.props.renderItem(item);
    //         // console.log(label);
    //         return (
    //             <li
    //                 key={id}
    //                 className='char-list__item'
    //                 // onClick={() => this.props.onItemSelected(id)}
    //                 label={label}></li>
    //         )
    //     });
    // }

    render () {

        const {data: {results}} = this.props;
        const items = this.renderItems(results);

        return (
            <div className="char-list">
                {/* {items} */}
                <li></li>
            </div>
        )
    }
}

const WithData = (View) => {
    return class extends Component {

        state = {
            data: null
        }

        // static defaultProps = {
        //     onItemSelected: () => {}
        // }

        // static propTypes = {
        //     onItemSelected: PropTypes.func
        // }

        componentDidMount () {
            const {getData} = this.props;

            getData()
                .then((data) => {
                    // console.log(data);
                    this.setState({
                        data,
                        error: false
                    })
                })
                .catch()
        }

        componentDidCatch () {
            this.setState({
                data: null,
                error: true
            })
        }

        render () {
            const {data, error} = this.state;
            console.log('view: ', data);

            if (error) {
                return <ErrorMessage/>
            }

            // if (!data) {
            //     return <Spinner/>
            // }

            return <View {...this.props} data={data}/>
        }

    }
}

export default WithData(CharList);
