export default class ServicePokedex {
    constructor() {
        this._url = 'https://pokeapi.co/api/v2';
    }

    getResources = async (id) => {
        const res = await fetch(`${this._url}${id}`);
        return await res.json();
    }

    getAllChars = async () => {
        const res = await this.getResources(`/pokemon/`);
        return res.map(this._transformCharacter);
    }

    getChar = async (id) => {
        const char = await this.getResources(`/pokemon/${id}`);
        console.log(char);
        return this._transformChar(char);
    }

    // // isSet(data) {
    // //     if (data) {
    // //         return data;
    // //     } else {
    // //         return 'no data!';
    // //     }
    // // }

    _transformChar = (char) => {
        return {
            id: char.id,
            name: char.name,
            types: char.types
            // img: this.isSet(char.sprites),
            // types: this.isSet(char.types)
        }
    }

}
