import styled from 'styled-components';

import './char-list-item.css';

const ImgWrapper = styled.div`
    display: flex;
    width: 100%;
    justify-content: center;
    align-items: center;
    overflow: hidden;
`;

const Sprite = styled.img`
    width: 60%;
    object-fit: cover;
`;

export default class CharListItem extends Component{

