            nameNew,
            charID,
            imgURL
        })

    }

    render () {
        // const charTypes = (
        //     <CharTypes
        //         getData={this.SerrvicePokedex.getResources}/>
        // )

        return (
            <div className="char-list__item">
                <ImgWrapper>
                    <Sprite
                        src={this.state.imgURL}/>
                    {/* <img src={this.state.imgURL} alt=""/> */}
                </ImgWrapper>
                <h3 className='char__title'>{this.state.nameNew}</h3>
                <CharTypes/>

            </div>            
        )

    }
}
