export default class CharCardDescription extends Component {

    ServicePokedex = new ServicePokedex();

    constructor (props) {
        super(props);

        this.state = {
            char: {},
            error: false,
            imgURL: '',
            // nameNew: ''
        }
    }

    componentDidMount () {
        this.updateItem();
    }

    componentDidUpdate (prevProps) {
        if (this.props.charId !== prevProps.charId) {
            this.updateItem();
        }
    }

    onCharDetailsLoaded = (char) => {
        this.setState({
            char
        });
    }

    updateItem () {
        const {charId, getData} = this.props;

        const imgURL = `https://pokeres.bastionbot.org/images/pokemon/${charId}.png`;

        this.setState({
            imgURL
        });

        getData(charId)
            .then(this.onCharDetailsLoaded)
            .catch(() => this.onError());
    }

    onError () {
        this.setState({
            char: [],
            imgURL: '',
            error: true
        });
    }

    // renderItems = (arr) => {
    //     return arr.map((item) => {
    //         const {slot, type} = item;  
    //         const {name} = type;          

    //         return (
    //             <TypeListItem
    //                 key={slot}>
    //                     {name}
    //             </TypeListItem>
    //         )
    //     })
    // }


    render () {

        const {char, imgURL} = this.state;
        const {name, types, moves, weight} = char;

        console.log(char);
        console.log('Name:', name);
        console.log('Types:', types);
        console.log('Moves:', moves);
        console.log('Weight:', weight);



        // attack, defence, hp, sp attack, sp defence, speed, weight, total moves 

        // const typesList = types.map((item) => 
        //     (
        //         <span>{item.type.name}</span>
        //     ))


        return (
            <CharDescriptionWrapper>
                <CharDescription>
                    <ImgWrapper>
                        <Sprite src={imgURL} alt=""/>
                    </ImgWrapper>
                    <CardTitle>{name}</CardTitle>
                    <table border='1' width='100%'>
                        <tbody>
                        <tr>
                            <th>Type</th>
                            <td>{types.type.name}</td>
                        </tr>
                        <tr>
                            <th>Attack</th>
                            <td>Value</td>
                        </tr>
                        <tr>
                            <th>Defence</th>
                            <td>Value</td>
                        </tr>
                        <tr>
                            <th>HP</th>
                            <td>Value</td>
                        </tr>
                        <tr>
                            <th>SP Attack</th>
                            <td>Value</td>
                        </tr>
                        <tr>
                            <th>SP Defence</th>
                            <td>Value</td>
                        </tr>
                        <tr>
                            <th>Speed</th>
                            <td>Value</td>
                        </tr>
                        <tr>
                            <th>Weight</th>
                            <td>Value</td>
                        </tr>
                        <tr>
                            <th>Total moves</th>
                            <td>Value</td>
                        </tr>

                        </tbody>                        
                    </table>
                </CharDescription>   
            </CharDescriptionWrapper>
        )
    }
}
