import React, { Component } from 'react';
import styled from  'styled-components';

// import getVars from '../../vars';

const COLORS = {
    fire: '#FF5300',
    grass: '#FF5300',
    electric: '#5ED1BA',
    water: '#1240AB',
    ground: '#A64B00',
    rock: '#6A8D8D',
    fairy: '#6AFF00',
    poison: '#00FF00',
    bug: '#BC00FF',
    dragon: '#FFE340',
    physic: '#FF40FF',
    flying: '#69C9BC',
    fighting: '#A60004',
    normal: '#FFFFFF'
};

const COLORS_KEYS = Object.keys(COLORS);
const COLORS_VALUES = Object.values(COLORS);

const TypeList = styled.ul`
    display: flex;
    width: 100%;
    justify-content: space-evenly;
    align-items: center;
    margin: 1rem 0;
    list-style: none;
`;

const TypeListItem = styled.li`
    display: inline-block;
    text-align: center;
    // border: 1px solid orangered;
    margin: 0.25rem;
    padding: 0.25rem 0.75rem;
    border-radius: 4px;
    transition: all 0.3s ease-in-out;

    &:hover {
        cursor: pointer;
        color: white;
    }

    background-color: ${props => props.bgColor}
`;

class CharTypes extends Component {

    renderItems = (arr) => {
        return arr.map((item, index) => {

            const {name} = item.type;            
            let bgColor;

            if (COLORS_KEYS.includes(name)) {
                let bgColor = COLORS_VALUES;
                // console.log(bgColor);
                return bgColor;
            } 

            // console.log(name);

            return (
                <TypeListItem
                    bg={bgColor}
                    key={index}>
                        {name}
                </TypeListItem>
            )
        })
    }

    render () {
        const {types} = this.props;

        // console.log(getVars.COLORS);

        const listItems = this.renderItems(types);

        return (
            <TypeList>
                {listItems}
            </TypeList>
        )
    }
}

export default CharTypes;
