export default class CharCardDescription extends Component {

    ServicePokedex = new ServicePokedex();

    constructor (props) {
        super(props);

        this.state = {
            char: {},
            error: false,
            imgURL: '',
            charId: this.props.charId
            // nameNew: ''
        }
    }

    onCharDetailsLoaded = (char) => {
        this.setState({
            char
        });
    }    

    componentDidMount () {
        const {charId, getData} = this.props;
        console.log(getData(charId));


        getData(charId)
        .then(this.onCharDetailsLoaded)
        .catch(() => this.onError());

        this.updateItem();
    }

    componentDidUpdate (prevProps) {
        if (this.props.charId !== prevProps.charId) {
            this.updateItem();
        }
    }

    updateItem () {
        const {charId, getData} = this.props;
        // if (!charId) return;
        const imgURL = `https://pokeres.bastionbot.org/images/pokemon/${charId}.png`;

        this.setState({
            imgURL
        });

        getData(charId)
            .then(this.onCharDetailsLoaded)
            .catch(() => this.onError());
    }

    onError () {
        this.setState({
            char: null,
            imgURL: '',
            error: true
        });
    }

    // renderItems = (arr) => {
    //     return arr.map((item) => {
    //         const {slot, type} = item;  
    //         const {name} = type;          

    //         return (
    //             <span
    //                 key={slot}>
    //                     {name}
    //             </span>
    //         )
    //     })
    // }


    render () {

        const {char, imgURL} = this.state;
        const {name, types, moves, weight} = char;

        console.log(char);
        // console.log('Name:', name);
        // console.log('Types:', types);
        // console.log('Moves:', moves);
        // console.log('Weight:', weight);

        // const itemList = this.renderItems(types);

        // attack, defence, hp, sp attack, sp defence, speed, weight, total moves 
        return (
            <CharDescriptionWrapper>
                <CharDescription>
                    <ImgWrapper>
                        <Sprite src={imgURL} alt=""/>
                    </ImgWrapper>
                    <CardTitle>{name}</CardTitle>
                    <table border='1' width='100%'>
                        <tbody>
                        <tr>
                            <th>Type</th>
                            {/* <td>{itemList}</td> */}
                        </tr>
                        <tr>
                            <th>Attack</th>
                            <td>Value</td>
                        </tr>
                        <tr>
                            <th>Defence</th>
                            <td>Value</td>
                        </tr>
                        <tr>
                            <th>HP</th>
                            <td>Value</td>
                        </tr>
                        <tr>
                            <th>SP Attack</th>
                            <td>Value</td>
                        </tr>
                        <tr>
                            <th>SP Defence</th>
                            <td>Value</td>
                        </tr>
                        <tr>
                            <th>Speed</th>
                            <td>Value</td>
                        </tr>
                        <tr>
                            <th>Weight</th>
                            <td>Value</td>
                        </tr>
                        <tr>
                            <th>Total moves</th>
                            <td>Value</td>
                        </tr>

                        </tbody>                        
                    </table>
                </CharDescription>   
            </CharDescriptionWrapper>
        )
    }
}
