    //                 label={label}/>
    //         )
    //     });
    // }


    render () {

        const {data} = this.props;
        console.log(data);
        // const items = this.renderItems(data);

        return (
            <div className="char-list">
                {/* {items} */}
                <CharListItem
                    // key={id}
                    // className='char-list__item'
                    // onClick={() => this.props.onItemSelected(id)}
                    // label={label}/>
            />
            </div>
        )
    }
}

const WithData = (View) => {
    return class extends Component {

        state = {
            data: null,
            error: false
        }

        static defaultProps = {
            onItemSelected: () => {}
        }

        static propTypes = {
            onItemSelected: PropTypes.func
        }

        componentDidMount () {
            const {getData} = this.props;

            getData()
                .then((data) => {
                    this.setState({
                        data,
                        error: false
                    })
                })
                .catch()
        }

        componentDidCatch () {
            this.setState({
                data: null,
                error: true
            })
        }

        render () {
            const {data, error} = this.state;

            if (error) {
                return <ErrorMessage/>
            }

            // if (!data) {
            //     return <Spinner/>
            // }

            return <View {...this.props} data={data}/>
        }

    }
}

export default withData(CharList);
