    //         return data;
    //     } else {
    //         return 'no data!';
    //     }
    // }

    _transformChar = (char) => {
        return {
            // id: this._extractId(char),
            name: char.name,
            // img: this.isSet(char.sprites),
            // types: this.isSet(char.types)
        }
    }

}
