import React, { Component } from 'react';
import CharListItem from '../char-list-item';
import CharCardDescription from '../char-description';
import styled from 'styled-components';

import ServicePokedex from '../../services';

const CardContainer = styled.section`
    display: grid;
    width: 100%;
    grid-template-columns: repeat(2, 1fr);
    grid-gap: 2.5rem;
`;

const CardBox = styled.div`
    display: grid;
    grid-template-columns: repeat(3, 1fr);
    width: 100%;
    grid-gap: 1.5rem;
    justify-content: center;
    align-items: center;
`;

const LoadMoreBtn = styled.button`
    grid-column: 1/2;
`;

// const CharDescription = styled.section`
//     display: flex;
//     width: 100%;
//     justify-content: center;
//     align-items: center;
// `;

export default class CharContainer extends Component {

    state = {
        // url: 'https://pokeapi.co/api/v2/pokemon/',
        chars: null,
        error: null,
        isLoaded: false,
        selectedChar: 1
    }

    componentDidMount () {
        fetch(this.state.url)
        .then(res => res.json())
        .then(
            (item) => {
                this.setState({
                    isLoaded: true,
                    chars: item.results
                })
            },
            (error) => {
                this.setState({
                    isLoaded: true,
                    error
                })
            }
        )
    }

    onCharSelected = (id) => {
        console.log('sadas');
        this.setState({
            selectedChar: id
        })
    }

    // getChar = async (id) => {
    //     const char = await this.getResource(`/pokemon/${id}`)
    //     return this._transformChar(char);
    // }


    render () {
        const {error, isLoaded, chars} = this.state;

        const charList = () => {
            if (error) {
                return <p>Error {error.message}</p>
            } else if (!isLoaded) {
                return <h1>Loading...</h1>
            } else {
                const cardItems = chars.map(item => 
                (
                    <CharListItem
                        key={item.name}
                        name={item.name}
                        url={item.url}
                        onCharSelected={this.onCharSelected}/>
                ));

                return (
                    <CardBox className="char-list">
                        {cardItems}
                    </CardBox>
                )
            }
        }

        const charDescription = (
            <CharCardDescription
                charId={this.state.selectedChar}
            />
        )

        return (
            <CardContainer className='char-container'>
                {charList()}
                {charDescription}
                <LoadMoreBtn>Load more</LoadMoreBtn>
            </CardContainer>
        )        
    }
}
