import React, { Component } from 'react';
import styled from 'styled-components';

import ServicePokedex from '../../services';

const CharDescriptionWrapper = styled.div`
    display: flex;
    width: 100%;
    justify-content: center;
    align-items: center;
`;

const CharDescription = styled.div`
    position: sticky;
    top: 50%;
    transform: translate(25%, -50%);
    height: 60vh;
    display: flex;
    flex-direction: column;
    width: 60%;
    justify-content: center;
    align-items: center;
`;

const ImgWrapper = styled.div`
    display: flex;
    width: 100%;
    justify-content: center;
    align-items: center;
    overflow: hidden;
`;

const Sprite = styled.img`
    width: 75%;
    object-fit: cover;
`;

const CardTitle = styled.h3`
    display: flex;
    width: 100%;
    justify-content: center;
    align-items: center;
    margin-top: 1rem;

    text-align: center;
    font-size: 1.25rem;
    font-weight: 600;
`;

export default class CharCardDescription extends Component {

    ServicePokedex = new ServicePokedex();

    constructor (props) {
        super(props);

        this.state = {
            char: [],
            error: false,
            imgURL: '',
            // nameNew: ''
        }
    }

    componentDidMount () {
        this.updateItem();
    }

    componentDidUpdate (prevProps) {
        if (this.props.charId !== prevProps.charId) {
            this.updateItem();
        }
    }

    onCharDetailsLoaded = (char) => {
        this.setState({
            char
        });
    }

    updateItem () {
        const {charId, getData} = this.props;

        if (!charId) return;
        const imgURL = `https://pokeres.bastionbot.org/images/pokemon/${charId}.png`;

        this.setState({
            imgURL
        });

        getData(charId)
            .then(this.onCharDetailsLoaded)
            .catch(() => this.onError());
    }

    onError () {
        this.setState({
            char: [],
            imgURL: '',
            error: true
        });
    }

    render () {
        const {char, imgURL} = this.state;
        const {name, types} = char;

        // attack, defence, hp, sp attack, sp defence, speed, weight, total moves 

        return (
            <CharDescriptionWrapper>
                <CharDescription>
                    <ImgWrapper>
                        <Sprite src={imgURL} alt=""/>
                    </ImgWrapper>
                    <CardTitle>{name}</CardTitle>
                    <table>
                        <tr>
                            <th>
                                {
                                    types.map((item) => 
                                    (
                                        <span>{item.type.name}</span>
                                    ))
                                }
                            </th>
                        </tr>  

                    </table>
                </CharDescription>   
            </CharDescriptionWrapper>
        )
    }
}
