import React, { Component } from 'react';
import CharTypes from '../char-types';
import styled from 'styled-components';

import spinner from '../spinner/Spinner-5.gif';


const Card = styled.div`
    width: 100%;
    display: flex;
    flex-direction: column;
    justify-content: center;
    align-items: center;

    border-radius: 4px;
    box-shadow: 10px 10px 15px 2.5px rgba(0,0,0,0.25);
    transition: all 0.3s ease-in-out;

    &:hover {
        background-color: rgba(0, 0, 0, 0.15);
    }
`;

const ImgWrapper = styled.div`
    display: flex;
    width: 100%;
    justify-content: center;
    align-items: center;
    overflow: hidden;
`;

const Sprite = styled.img`
    width: 60%;
    object-fit: cover;
`;

const CardTitle = styled.h3`
    display: flex;
    width: 100%;
    justify-content: center;
    align-items: center;
    margin-top: 1rem;

    text-align: center;
    font-size: 1.25rem;
    font-weight: 400;
`;

export default class CharListItem extends Component{
    state = {
        nameNew: '',
        imgURL: '',
        charID: '',
        imgLoading: true,
        manyRequests: false,

        types: {}
    }

    componentDidMount () {
        const {name, url} = this.props;

        const charID = url.split('/')[url.split('/').length - 2];
        const imgURL = `https://pokeres.bastionbot.org/images/pokemon/${charID}.png`;
        const nameNew = name[0].toUpperCase() + name.slice(1)
        // const types = {}

        this.setState({
            nameNew,
            charID,
            imgURL
        })

    }

    render () {

        return (
            <Card className="char-list__item">
                <ImgWrapper>
                    {
                        this.state.imgLoading ? (
                            <img
                                src={spinner}
                                style={{width: '5rem', height: '5rem'}}
                                className='spinner'
                                alt='Loading'/>
                        ) : null
                    }

                    <Sprite
                        className='char-img'
                        src={this.state.imgURL}
                        alt='Hero image'
                        onLoad={() => this.setState({imgLoading: false})}
                        onError={() => this.setState({manyRequests: true})}
                        style={
                            this.state.manyRequests
                                ? {display: 'none'}
                                : this.state.imgLoading
                                ? null
                                : {display: 'block'}
                        }
                        />
                    {this.state.manyRequests ? (
                        <h4>To many requests</h4>
                    ) : null}
                </ImgWrapper>
                <CardTitle className='char__title'>{this.state.nameNew}</CardTitle>
                <CharTypes/>

            </Card>            
        )

    }
}
